import React, { useEffect, useState } from 'react'
import { useAuth } from '../context/AuthContext'
import { Button, Form, Grid, Segment } from 'semantic-ui-react'
import { Link } from 'react-router-dom';
import 'semantic-ui-css/semantic.min.css';

function HomePage() {
  const Auth = useAuth();
  const user = Auth.getUser();
  const { userLogout } = useAuth();
  console.log(user);

  const handleSubmit = async (e) => {
    e.preventDefault();
    userLogout();
  }

  return (
    <Grid textAlign='center' style={{ height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
      <Grid.Column style={{ maxWidth: 450 }}>
        <h1>Welcome {user.name}!</h1>
        <h4>Here are your details:</h4>
        <Form size='large' onSubmit={handleSubmit}>
          <Segment>
            <h3>Name: {user.name}</h3>
            <h3>Username: {user.username}</h3>
            <h3>Role: {user.role}</h3>
            {user && user.role === 'MANAGER' && (
              <div style={{marginTop: 40, marginBottom: 20}}>
                <Link to="/restricted" style={{fontSize: 20}}>Visit Restricted Page</Link>
              </div>
            )}
          </Segment>
          <Button color='pink' fluid size='large'>Logout</Button>
        </Form>
      </Grid.Column>
    </Grid>
  )
}

export default HomePage
