import React, { useEffect, useState } from 'react'
import { Navigate } from 'react-router-dom'
import { Container } from 'semantic-ui-react'
import { useAuth } from '../context/AuthContext'
import { handleLogError } from '../misc/Helpers'
import { Button, Form, Grid, Segment, Message } from 'semantic-ui-react'
import { Link } from 'react-router-dom';
import 'semantic-ui-css/semantic.min.css';

function HomePage() {
  const Auth = useAuth();
  const user = Auth.getUser();
  const { userLogout } = useAuth();
  console.log(user);

  const handleSubmit = async (e) => {
    e.preventDefault();
    userLogout();
  }

  return (
    <Grid textAlign='center' style={{ height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
      <Grid.Column style={{ maxWidth: 450 }}>
        <Form size='large' onSubmit={handleSubmit}>
          <Segment>
            <h1>Name: {user.name}</h1>
            <h1>Username: {user.username}</h1>
            <h1>Role: {user.role}</h1>
            {user && user.role === 'MANAGER' && (<Link to="/restricted" style={{fontSize: 20, marginTop: 20}}>Visit Restricted Page</Link>)}
            <Button color='blue' fluid size='large'>Logout</Button>
          </Segment>
        </Form>
      </Grid.Column>
    </Grid>
  )
}

export default HomePage
